export default class TypeChecker{isType(r,n){return typeof r===n}isString(r){return this.isType(r,"string")}isNumber(r){return this.isType(r,"number")}isFunction(r){return typeof r==="function"}isArray(r){return Array.isArray(r)}isNull(r){return r===null}isUndefined(r){return r===undefined}isError(r){return r instanceof Error}isPromise(r){return r instanceof Promise}isMap(r){return r instanceof Map}isObject(r){return typeof r==="object"&&!Array.isArray(r)}isAsyncFunction(r){return r[Symbol.toStringTag]==="AsyncFunction"}}